alias apinitnpm="ap_func_init_npm"
ap_func_init_npm() {
    if alias apinitnpmshare &>/dev/null; then
        apinitnpmshare
    fi

    if alias apinitnpmcommon &>/dev/null; then
        apinitnpmcommon
    fi

    if alias apinitnpmmain &>/dev/null; then
        apinitnpmmain
    fi
}

