alias apinitpnpm="ap_func_init_pnpm"
ap_func_init_pnpm() {
    if alias apinitpnpmshare &>/dev/null; then
        apinitpnpmshare
    fi

    if alias apinitpnpmcommon &>/dev/null; then
        apinitpnpmcommon
    fi

    if alias apinitpnpmmain &>/dev/null; then
        apinitpnpmmain
    fi
}

